// Front-end code using React

import React, { useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
function ArticleView() {
    const [pass_key, setPassKey] = useState('');
    const { link_name } = useParams();
    const [content, setContent] = useState('');
    const linkRef = useRef();

    const retrieveContent = (link_name, pass_key) => {
      return axios
        .post(`http://localhost:3000/api/link/${link_name}`, {
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ pass_key }),
        })
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          console.log('Content:', data);
          // Handle the retrieved content here
        })
        .catch((error) => {
          console.error('Error:', error);
          // Handle the error here
        });
    };

    retrieveContent('link_name', 'pass_key')
  .then((content) => {
    // Do something with the retrieved content
    console.log(content);
  })
  .catch((error) => {
    // Handle the error
    console.log(error);
  });



  return (
    <form id="retrive">
        <div>
          <label htmlFor="pass_key">Your Link Name is : {link_name}</label>
          <input
            className='input-pass'
            type="password"
            placeholder="Pass key For This Link :"
            value={pass_key}
            ref={linkRef}
            onChange={(e) => setPassKey(e.target.value)}
            required
          />
        </div>

        <div>
        <button onClick={retrieveContent}>Retrieve Content</button>
        <p>{content}</p>
        </div>
    </form>
  );
}

export default ArticleView;
