// Front-end code using React

import React, { useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
function ArticleView() {
    const [pass_key, setPassKey] = useState('');
    const { link_name } = useParams();
    const [content, setContent] = useState('');
    const linkRef = useRef();


    const handleRetrieveContent = async (link_name, pass_key) => {
        try {
            const response = await axios.get(`http://localhost:3000/api/link/${link_name}/${pass_key}`);
            const data = await response.data;
            setContent(data);
            console.log('Content:', data);
            // Handle the retrieved content here
        } catch (error) {
            console.error('Error:', error);
            console.log("ERROR:", error.message);
            // Handle the error here
        }
};


const retrieveContent = (link_name, pass_key) => {
  return axios
    .get(`http://localhost:3000/api/link/${link_name}/${pass_key}`)
    .then((response) => {
      const data = response.data;
      console.log('Content:', data);
      // Handle the retrieved content here
      return data;
    })
    .catch((error) => {
      console.error('Error:', error);
      console.log('ERROR:', error.message);
      // Handle the error here
      throw error; // Re-throw the error to propagate it further if needed
    });
};

retrieveContent(link_name, pass_key)
  .then((content) => {
    // Do something with the retrieved content
    console.log("AAAAAAAAAAAAAAAAAAAAAAAAA");
  })
  .catch((error) => {
    // Handle the error
    console.log("EEEEEEEEEEEEEEEEEE");
  });




    const handleClick = (e) => {
      e.preventDefault();
      link_name: linkRef.current.value
      retrieveContent(link_name, pass_key)
      .then((content) => {
        // Do something with the retrieved content
        console.log("AAAAAAAAAAAAAAAAAAAAAAAAA");
      })
      .catch((error) => {
        // Handle the error
        console.log("EEEEEEEEEEEEEEEEEE");
      });
      };

  return (
    <form id="retrive">
        <div>
          <label htmlFor="pass_key">Your Link Name is : {link_name}</label>
          <input
            className='input-pass'
            type="password"
            placeholder="Pass key For This Link :"
            value={pass_key}
            ref={linkRef}
            onChange={(e) => setPassKey(e.target.value)}
            required
          />
        </div>

        <div>
        <button onClick={retrieveContent}>Retrieve Content</button>
        <p>Content: {content}</p>
        </div>
    </form>
  );
}

export default ArticleView;
