import React, { useState, useEffect } from 'react';
import './contentViewer.css';

function ContentViewer() {
  const [pass_key, setPassKey] = useState('');
  const [link_name, setLinkName] = useState('');
  const [content, setContent] = useState('');

  useEffect(() => {
    // Extract the content URL from the browser's tab search bar
    const currentURL = window.location.href;
    const parts = currentURL.split('/'); // Split the URL by '/'
    const name = parts[parts.length - 1]; // Get the last part

    // Update the contentURL state with the extracted value
    setLinkName(name);
  }, []);

  const handleVerifyContent = async () => {
    try {
      // Send a request to your API to verify the content URL and pass_key
      const response = await fetch(`/api/link/${link_name}/${pass_key}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ link_name: link_name, pass_key: pass_key }),
      });

      if (response.ok) {
        const data = await response.json();
        setContent(data.content);
      } else {
        alert('Incorrect pass_key or content URL');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  }

  return (
<div id="subscription_area">
  <div className="container">
    <div className="row">
      <div className="col-sm-12">
        <div className="subscribe_now">
          <h4>subscribe</h4>
          <p>Get connected to our insights</p>
          <form className="subscribe_form">
            <div className="input-group">
               <input type="text" className="form-control" name="email" placeholder="Enter your email"/>
               <span className="input-group-btn">
                    <button className="btn btn-default" type="button">subscribe</button>
               </span>
            </div>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
  );
}

export default ContentViewer;
