// Front-end code using React

import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
function ArticleView() {
    const [pass_key, setPassKey] = useState('');
    const { link_name } = useParams();
    const [content, setContent] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');


    const handleRetrieveContent = async (link_name, pass_key) => {
        try {
            const response = await axios.get(`http://localhost:3000/api/link/${link_name}/${pass_key}`);
            const data = await response.data;
            setContent(data);
            console.log('Content:', data);
            // Handle the retrieved content here
        } catch (error) {
            console.error('Error:', error);
            console.log("ERROR:", error.message);
            // Handle the error here
        }
};

const handleLogin = async () => {
  try {
    const response = await fetch('http://localhost:3000/api/link', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ link_name, pass_key }),
    });

    const data = await response.json();

    if (response.ok) {
      setMessage(data.message);
      setError('');
    } else {
      setMessage('');
      setError(data.error);
    }
  } catch (error) {
    console.error('Error during login:', error.message);
    setMessage('');
    setError('An error occurred during login');
  }
}

    const handleClick = () => {
        handleRetrieveContent(link_name, pass_key);
      };

  return (
    <form id="retrive">
        <div>
          <label htmlFor="pass_key">Your Link Name is : {link_name}</label>
          <input
            className='input-pass'
            type="password"
            placeholder="Pass key For This Link :"
            value={pass_key}
            onChange={(e) => setPassKey(e.target.value)}
            required
          />
        </div>

        <div>
        <button onClick={handleLogin}>Retrieve Content</button>

        </div>
        <div>
        {message && <p style={{ color: 'green' }}>{message}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
            </div>
    </form>
  );
}

export default ArticleView;
