// Front-end code using React

import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';



function ArticleView() {
    const [pass, setPassKey] = useState('');
    const { link_name } = useParams();
    const [content, setContent] = useState('');

//////////////////////////////////////////////

    const handleChange = (e) => {
      e.preventDefault();
      setPassKey(e.target.value);
    };



    ///////////////////////////////////

    const retrieveContent = (link_name, pass_key) => {
      return axios
        .get(`http://localhost:3000/api/link/${link_name}/${pass_key}`)
        .then((response) => {
          const data = response.data;
          console.log('Content:', data);
          setContent(data);
          // Handle the retrieved content here
          return data;
        })
        .catch((error) => {
          console.error('Error:', error);
          console.log('ERROR:', error.message);
          // Handle the error here
          throw error; // Re-throw the error to propagate it further if needed
        });
    };

    retrieveContent(link_name, pass)
    .then((content) => {
      // Do something with the retrieved content
      console.log("P");

    })
    .catch((error) => {
      // Handle the error
      console.log("E");
    });

    const handleSubmit = async (e) => {
      e.preventDefault();
      setTimeout(() => {
      retrieveContent(link_name, pass);
    }, 3000); 
};

  return (
    <form id="retrive" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="pass_key">Your Link Name is : {link_name}</label>
          <input
            className='input-pass'
            type="password"
            placeholder="Pass key For This Link :"
            value={pass}
            onChange={handleChange}
            required
          />
        </div>

        <div>
        <button type='submit'>Retrieve Content</button>
        <p>{content}</p>
        </div>
    </form>
  );
}

export default ArticleView;
